library(data.table)
library(dplyr)
remotes::install_github("mattlee821/functions")
library(functions)
library(TwoSampleMR)

# Load and prepare exposure data
data_exposure_protein <- fread("data/raw/GWAS/plasma-proteins.txt")
data_exposure_protein <- data_exposure_protein %>%
  filter(exposure == "18878_15_GREM1_GREM1")

# Load and prepare outcome data
data_outcome_crc <- TwoSampleMR::read_outcome_data(filename = "data/raw/GWAS/crc_combined_GECCO_allancestries.txt.gz", 
                                               sep = "\t",
                                               snps = data_exposure_protein$SNP, 
                                               phenotype_col = "phenotype", 
                                               id_col = "phenotype", 
                                               snp_col = "SNP", 
                                               beta_col = "BETA", 
                                               se_col = "SE", 
                                               pval_col = "P",
                                               effect_allele_col = "EA", 
                                               other_allele_col = "OA", 
                                               chr_col = "CHR", 
                                               pos_col = "POS",
                                               min_pval = 1e-200,
                                               log_pval = FALSE)

# Harmonise data
data_harmonise_protein_crc <- harmonise_data(
  exposure_dat = data_exposure_protein, 
  outcome_dat = data_outcome_crc
)

# Perform MR analysis
data_mr_protein_crc <- mr(data_harmonise_protein_crc)

# Create MR results table
table_mr_protein_crc <- data_mr_protein_crc %>%
  select(method, b, se, pval) %>%
  rename(Method = method, Estimate = b, Std_Error = se, P_Value = pval)
print(table_mr_protein_crc)

