library(data.table)
library(dplyr)
remotes::install_github("mattlee821/functions")
# install.packages("remotes")
# remotes::install_github("MRCIEU/TwoSampleMR")
library(functions) 
library(TwoSampleMR)

# Load and prepare exposure data
data_exposure_protein <- fread("data/raw/GWAS/plasma-proteins.txt")
data_exposure_protein <- data_exposure_protein %>%
  filter(exposure == "18878_15_GREM1_GREM1")

# Shorten names
shorten_exposure_name <- function(name) {
  sub("^\\d+_\\d+_(.*?)_.*$", "\\1", name)
}

# Apply the function to shorten the exposure name
data_exposure_protein$exposure <- shorten_exposure_name(data_exposure_protein$exposure)
head(data_exposure_protein)
# Load and prepare outcome data
data_outcome_crc <- TwoSampleMR::read_outcome_data(filename = "data/raw/GWAS/crc_combined_GECCO_allancestries.txt.gz", 
                                               sep = "\t",
                                               snps = data_exposure_protein$SNP, 
                                               phenotype_col = "phenotype", 
                                               id_col = "phenotype", 
                                               snp_col = "SNP", 
                                               beta_col = "BETA", 
                                               se_col = "SE", 
                                               pval_col = "P",
                                               effect_allele_col = "EA", 
                                               other_allele_col = "OA", 
                                               chr_col = "CHR", 
                                               pos_col = "POS",
                                               min_pval = 1e-200,
                                               log_pval = FALSE)

# Harmonise data
data_harmonise_protein_crc <- harmonise_data(
  exposure_dat = data_exposure_protein, 
  outcome_dat = data_outcome_crc
)
write.table(data_harmonise_protein_crc, "analysis/001_MR_proteins-CRC/data_harmonise_protein-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)

# Perform MR analysis
data_mr_protein_crc <- mr(data_harmonise_protein_crc)
print(data_mr_protein_crc)

# Create MR results table
table_mr_protein_crc <- data_mr_protein_crc %>%
  select(exposure, outcome, method, b, se, pval) %>%
  rename(Exposure = exposure, Outcome = outcome, Method = method, Estimate = b, Std_Error = se, P_Value = pval)
print(table_mr_protein_crc)
write.table(table_mr_protein_crc, "analysis/001_MR_proteins-CRC/table_mr_protein-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)
