library(data.table)
library(dplyr)
#remotes::install_github("mattlee821/functions")
# install.packages("remotes")
# remotes::install_github("MRCIEU/TwoSampleMR")
library(functions)
library(TwoSampleMR)
library(ieugwasr)
library(data.table)
library(dplyr)
library(tidyverse)
library(tidyr)

# Load exposure data
data_exposure <- fread("data/raw/GWAS/colon-sigmoid_allpairs_ENSG00000166923.10.txt")
data_exposure <- data_exposure %>%
  filter(pval_nominal <= 5e-4)
data_exposure_ref<- fread("data/raw/GWAS/reference-chr15.txt") #load reference data
data_exposure <- data_exposure %>%
  left_join(data_exposure_ref, by = "variant_id") %>%
  rename(
    exposure = gene_id,
    rsid = rs_id_dbSNP151_GRCh38p7,           
    beta.exposure = slope,                   
    se.exposure = slope_se,                  
    effect_allele.exposure = alt,     
    other_allele.exposure = ref,      
    eaf.exposure = maf,  
    pval = pval_nominal
  ) %>%
  mutate(
    id.exposure= exposure)

#clumping
data_exposure <- ld_clump(
  dat= data_exposure,
  plink_bin = genetics.binaRies::get_plink_binary(),
  bfile = "data/references/1000genomes/phase3/processed/ALL/ALL"
) %>%
  rename(SNP = rsid)

#Load and prepare outcome data
data_outcome <- TwoSampleMR::read_outcome_data(filename = "data/raw/GWAS/crc_combined_GECCO_allancestries.txt.gz", 
                                                   sep = "\t",
                                                   snps = data_exposure$SNP, 
                                                   phenotype_col = "phenotype", 
                                                   id_col = "phenotype", 
                                                   snp_col = "SNP", 
                                                   beta_col = "BETA", 
                                                   se_col = "SE", 
                                                   pval_col = "P",
                                                   effect_allele_col = "EA", 
                                                   other_allele_col = "OA", 
                                                   chr_col = "CHR", 
                                                   pos_col = "POS",
                                                   min_pval = 1e-200,
                                                   log_pval = FALSE)

head(data_outcome)

#MAF/eaf for outcome data ====
data_maf <- fread("data/references/1000genomes/phase3/processed/ALL/stats.stats")
data_maf <- data_maf %>%
  rename(SNP = Predictor,
         eaf.outcome = MAF ) %>%
  select(SNP, A1, A2, eaf.outcome) 
head(data_maf)

#Left join and update the eaf.outcome column directly
data_outcome <- data_outcome %>%
  left_join(data_maf, by = "SNP", suffix = c("", ".maf")) %>%
  mutate(eaf.outcome = ifelse(is.na(eaf.outcome), eaf.outcome.maf, eaf.outcome)) %>%
  select(-eaf.outcome.maf)  # remove the temporary column
head(data_outcome)

#Harmonise data
data_harmonise <- harmonise_data(
  exposure_dat = data_exposure, 
  outcome_dat = data_outcome
)
write.table(data_harmonise, "analysis/006_MR_gene-CRC/001_ENSG00000166923-10_sigmoid_data_harmonise_gene-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)

# Perform MR analysis
data_mr <- mr(data_harmonise)
print(data_mr)

# Create MR results table
table_mr <- data_mr %>%
  select(exposure, outcome, method, b, se, pval) %>%
  rename(Exposure = exposure, Outcome = outcome, Method = method, Estimate = b, Std_Error = se, P_Value = pval)
print(table_mr)
write.table(table_mr, "analysis/006_MR_gene-CRC/001_ENSG00000166923-10_sigmoid_table_mr_gene-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)
