library(data.table)
library(dplyr)
#remotes::install_github("mattlee821/functions")
# install.packages("remotes")
# remotes::install_github("MRCIEU/TwoSampleMR")
library(functions)
library(TwoSampleMR)

# Load and prepare exposure data
data_exposure_gene <- fread("data/raw/GWAS/colon-sigmoid_allpairs_ENSG00000166923.10.txt")
head(data_exposure_gene)

#Map SNP in GWAS dataset from plasma protein data
data_exposure_protein <- data_exposure_protein %>%
  filter(exposure == "18878_15_GREM1_GREM1") #load protein data and filter by protien
head(data_exposure_protein)
data_exposure_ref<- fread("data/raw/GWAS/reference-chr15.txt") #load reference data
data_exposure_combind <- data_exposure_gene %>%
  left_join(data_exposure_ref, by = "variant_id") #merge data between gene expression and reference data (to get SNP)
data_exposure_SNP <- data_exposure_combind %>%
  filter(rs_id_dbSNP151_GRCh38p7 == data_exposure_protein$SNP) #filter exposure data from SNP in GWAS protien

#Prepare exposure data 
data_exposure_final <- data_exposure_SNP %>%
  rename(
    SNP = rs_id_dbSNP151_GRCh38p7,           
    beta.exposure = slope,                   
    se.exposure = slope_se,                  
    effect_allele.exposure = alt,            
    other_allele.exposure = ref,
    eaf.exposure = maf,  #not sure if maf can serve as eaf
    pval.exposure = pval_nominal
  ) %>%
  mutate(
    id.exposure = data_exposure_protein$exposure,
    exposure = data_exposure_protein$exposure       
  )

#Load and prepare outcome data
data_outcome_crc <- TwoSampleMR::read_outcome_data(filename = "data/raw/GWAS/crc_combined_GECCO_allancestries.txt.gz", 
                                                   sep = "\t",
                                                   snps = data_exposure_gene$SNP, 
                                                   phenotype_col = "phenotype", 
                                                   id_col = "phenotype", 
                                                   snp_col = "SNP", 
                                                   beta_col = "BETA", 
                                                   se_col = "SE", 
                                                   pval_col = "P",
                                                   effect_allele_col = "EA", 
                                                   other_allele_col = "OA", 
                                                   chr_col = "CHR", 
                                                   pos_col = "POS",
                                                   min_pval = 1e-200,
                                                   log_pval = FALSE)

head(data_outcome_crc)
# Harmonise data
data_harmonise_gene_crc <- harmonise_data(
  exposure_dat = data_exposure_final, 
  outcome_dat = data_outcome_crc
)

# Perform MR analysis
data_mr_gene_crc <- mr(data_harmonise_gene_crc)
print(data_mr_gene_crc)
write.table(data_mr_gene_crc, "analysis/007_MR_gene-CRC/001_GREM1_data_harmonise_gene-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)

# Create MR results table
table_mr_gene_crc <- data_mr_gene_crc %>%
  select(exposure, outcome, method, b, se, pval) %>%
  rename(Exposure = exposure, Outcome = outcome, Method = method, Estimate = b, Std_Error = se, P_Value = pval)
print(table_mr_gene_crc)
write.table(table_mr_gene_crc, "analysis/007_MR_gene-CRC/001_GREM1_table_mr_gene-crc.txt", sep="\t", row.names = FALSE, quote = FALSE, col.names = TRUE)
